sections.css:
h1.sg,
h2.sg,
h3.sg,
h4.sg,
h5.sg {
  font-family: 'Merriweather';
  font-weight: 400;
  line-height: 1.5;
  padding: 0;
  color: #333;
}

a.sg {
  @include link-colors($pale-pink, $toledo, $pale-pink, $pale-pink, $toledo);
  transition: all 0.5s ease;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
  &:active {
    color: $default-action-dark;
  }
}


p.sg {
  @extend %no-dangling;
  margin-top: 0;
  &:last-child {
    margin-bottom: 0;
  }
  &.intro {
    @extend h3;
  }
}

code.sg,
pre.sg {
  background: #f7f2f5;
  font-size: 14px;
  padding: 4px;
}

li.sg {
  @include default-font;
  margin-left: 1em;
  padding: .2em 0;
}

strong.sg {
  @include default-font;
  font-weight: 700;
}

small.sg {
  @include default-font;
  color: #888;
}

button.sg {
  @include default-font;
  display: inline;

  margin-right: 8px;
  padding: 8px;

  cursor: pointer;

  color: white;
  border-radius: 5px;
  background-color: $default-action-color;

  &:hover {
    background-color: lighten($default-action-color, $action-color-change);
  }
  &:active {
    background-color: darken($default-action-color, $action-color-change);
  }

  &.sg-primary {
    color: white;
    background-color: $primary-action-color;
    &:hover {
      background-color: lighten($primary-action-color, $action-color-change);
    }
    &:active {
      background-color: darken($primary-action-color, $action-color-change);
    }
  }

  &:disabled {
    cursor: default;

    color: #999;
    background-color: $secondary-color;
  }
}

input.sg {
  @include default-font;
  font-family: $secondary-font;
  font-weight: 300;

  margin: 8px;
  padding: 8px;
  padding: .5em;

  border: 1px solid $secondary-color;

  &:focus {
    background-color: $tertiary-color;
    border: 1px solid $secondary-color;
  }
}

