sections.css:
%no-dangling {
  &:first-child, &:first-of-type {
    margin-top: 0;
  }
}
h1, h2, h3, h4, h5 {
  @extend %no-dangling;
  font-family: 'Merriweather';
  font-weight: 400;
  line-height: 1.5;
  padding: 0;
  color: #333;
  @include breakpoint($bp-medium-portrait) {
    @include rem(font-size, 17.5px);
  }
  @include breakpoint($bp-large)   {
    line-height: 1.58;
  }
}

h1, %h1 {
  @include rem(font-size, 16.25px);
  margin-bottom: 12px;
  @include breakpoint($bp-medium-portrait) {
    @include rem(font-size, 18.25px);
  }

  @include breakpoint($bp-large) {
    @include rem(font-size, 20px);
  }
}
h1.post-title {
  @include rem(font-size, 12px);
  margin-bottom: 26px;
  font-family: Helvetica Neue, Helvetica, Arial;
  font-weight: bold;
}
h1.page-title {
  @include rem(font-size, 14px);
  margin-bottom: 12px;
  padding-bottom: 12px;
  border-bottom: 1px solid #EBEBEB;
  @include breakpoint($bp-medium-portrait) {
    @include rem(font-size, 16px);
    margin-bottom: 26px;
    padding-bottom: 26px;
    border-bottom: 1px solid #EBEBEB;
  }
}
h2, %h2 {
  @include rem(font-size, 10px);
  margin-bottom: 12px;
  @include breakpoint($bp-medium-portrait) {
    @include rem(font-size, 12px);
  }
  @include breakpoint($bp-large) {
    @include rem(font-size, 14px);
  }
}

h3, %h3 {
  @include rem(font-size, 8.75px);
  margin-bottom: 12px;

  @include breakpoint($bp-medium-portrait) {
    @include rem(font-size, 9px);
  }

  @include breakpoint($bp-large) {
    @include rem(font-size, 10.25px);
  }
}

h4, %h4, h5, %h5 {
  @include rem(font-size, 11.25px);

  @include breakpoint($bp-medium-portrait) {
    @include rem(font-size, 17.5px);
  }

  @include breakpoint($bp-large) {
    @include rem(font-size, 13.75px);
  }
}

